import React, { useEffect, useState } from "react";
import axios from "axios";
import { useMoralis } from "react-moralis";
import { ethers } from "ethers";
import { Card, Image, Button, Divider } from "antd";
import { nftaddress, nftmarketaddress } from "../config";
import NFT from "../eSamudaayNFT_abi.json";
import Market from "../eSamudaayNFTMarket.json";

const { Meta } = Card;

const styles = {
  NFTs: {
    display: "flex",
    flexWrap: "wrap",
    WebkitBoxPack: "start",
    justifyContent: "flex-start",
    margin: "0 auto",
    maxWidth: "1000px",
    width: "100%",
    gap: "10px",
  },
};
function Home() {
  const [nfts, setNfts] = useState([]);
  useEffect(() => {
    loadNFTs();
  }, []);

  const { authenticate, isAuthenticated, account } = useMoralis();

  async function loadNFTs() {
    const provider = new ethers.providers.JsonRpcProvider(
      "https://rpc-mumbai.maticvigil.com"
    );
    const tokenContract = new ethers.Contract(nftaddress, NFT.abi, provider);
    const marketContract = new ethers.Contract(
      nftmarketaddress,
      Market.abi,
      provider
    );
    const data = await marketContract.fetchMarketItems();

    const items = await Promise.all(
      data.map(async (index) => {
        const tokenUri = await tokenContract.tokenURI(index.tokenId);
        const meta = await axios.get(tokenUri);
        let price = ethers.utils.formatUnits(index.price.toString(), "ether");
        let item = {
          price,
          itemId: index.itemId.toNumber(),
          seller: index.seller,
          owner: index.owner,
          image: meta.data.image,
          name: meta.data.name,
          description: meta.data.description,
        };
        return item;
      })
    );
    setNfts(items);
  }
  async function buyNft(nft) {
    try {
      const { ethereum } = window;
      if (ethereum) {
        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const connectedContract = new ethers.Contract(
          nftmarketaddress,
          Market.abi,
          signer
        );

        const price = ethers.utils.parseUnits(nft.price.toString(), "ether");
        const nftTxn = await connectedContract.createMarketSale(
          nftaddress,
          nft.itemId,
          {
            value: price,
          }
        );
        await nftTxn.wait();
        loadNFTs();
      } else {
        console.log("Ethereum object doesn't exist!");
      }
    } catch (error) {
      console.log(error);
    }
  }
  return (
    <div style={{ padding: "15px", maxWidth: "1030px", width: "100%" }}>
      <h1>Explore NFTs</h1>
      <Divider />
      <div style={styles.NFTs}>
        {nfts &&
          nfts.map((nft, index) => {
            return (
              <Card
                hoverable
                style={{
                  width: 240,
                  border: "2px solid #e7eaf3",
                }}
                cover={
                  <Image
                    preview={false}
                    src={nft?.image || "error"}
                    fallback="data:image/png;base64,"
                    alt=""
                    style={{ height: "300px" }}
                  />
                }
                key={index}
              >
                <Meta title={nft.name} description={`${nft.price} MATIC`} />
                <Divider />
                {!isAuthenticated || !account ? (
                  <Button
                    size="large"
                    type="primary"
                    style={{
                      width: "100%",
                      borderRadius: "0.5rem",
                      fontSize: "16px",
                      fontWeight: "500",
                    }}
                    onClick={async () => {
                      try {
                        await authenticate();
                      } catch (e) {
                        console.error(e);
                      }
                    }}
                  >
                    Connect Wallet
                  </Button>
                ) : (
                  <Button
                    style={{
                      width: "100%",
                      borderRadius: "0.5rem",
                      fontSize: "16px",
                      fontWeight: "500",
                    }}
                    onClick={() => buyNft(nft)}
                    type="primary"
                  >
                    Buy
                  </Button>
                )}
              </Card>
            );
          })}
      </div>
    </div>
  );
}

export default Home;
