import React, { useEffect, useState } from "react";
import axios from "axios";
import { ethers } from "ethers";
import { Card, Image, Divider } from "antd";
import { nftaddress, nftmarketaddress } from "../config";
import NFT from "../eSamudaayNFT_abi.json";
import Market from "../eSamudaayNFTMarket.json";

const { Meta } = Card;

const styles = {
  NFTs: {
    display: "flex",
    flexWrap: "wrap",
    WebkitBoxPack: "start",
    justifyContent: "flex-start",
    margin: "0 auto",
    maxWidth: "1000px",
    width: "100%",
    gap: "10px",
  },
};
function MyNFT() {
  const [nfts, setNfts] = useState([]);
  useEffect(() => {
    loadNFTs();
  }, []);

  async function loadNFTs() {
    try {
      const { ethereum } = window;
      if (ethereum) {
        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();

        const marketContract = new ethers.Contract(
          nftmarketaddress,
          Market.abi,
          signer
        );
        const tokenContract = new ethers.Contract(
          nftaddress,
          NFT.abi,
          provider
        );
        const data = await marketContract.fetchMyNFTs();
        console.log(`data is => ${data}`);

        const items = await Promise.all(
          data.map(async (index) => {
            console.log(
              `token iD is => ${index.tokenId}, seller is => ${index.seller}, owner is => ${index.owner}`
            );
            const tokenUri = await tokenContract.tokenURI(index.tokenId);
            console.log(`tokenUri => ${tokenUri}`);
            const metaData = await axios.get(tokenUri);
            console.log(`metaData => ${JSON.stringify(metaData)}`);
            let price = ethers.utils.formatUnits(
              index.price.toString(),
              "ether"
            );
            let item = {
              price,
              tokenId: index.tokenId.toNumber(),
              seller: index.seller,
              owner: index.owner,
              name: metaData.data.name,
              image: metaData.data.image,
            };
            return item;
          })
        );
        setNfts(items);
      } else {
        console.log("Ethereum object doesn't exist!");
      }
    } catch (error) {
      console.log(error);
    }
  }
  return (
    <div style={{ padding: "15px", maxWidth: "1030px", width: "100%" }}>
      <h1>My NFTs</h1>
      <Divider />
      <div style={styles.NFTs}>
        {nfts &&
          nfts.map((nft, index) => {
            return (
              <Card
                hoverable
                style={{ width: 240, border: "2px solid #e7eaf3" }}
                cover={
                  <Image
                    preview={false}
                    src={nft?.image || "error"}
                    fallback="data:image/png;base64,"
                    alt=""
                    style={{ height: "300px" }}
                  />
                }
                key={index}
              >
                <Meta title={nft.name} description={`${nft.price} MATIC`} />
              </Card>
            );
          })}
      </div>
    </div>
  );
}

export default MyNFT;
